class Circle{constructor(r){this.color=r}setColor(r){this.color=r}render(){return`<circle cx="150" cy="100" r="80" fill="${this.color}"/>`}}class Square{constructor(r){this.color=r}setColor(r){this.color=r}render(){return`<rect x="50" y="50" width="200" height="200" fill="${this.color}"/>`}}class Triangle{constructor(r){this.color=r}setColor(r){this.color=r}render(){return`<polygon points="0,200 150,0 300,200" fill="${this.color}"/>`}}module.exports={Circle:Circle,Square:Square,Triangle:Triangle};